USE trainme_db;
ALTER TABLE campaigns ADD COLUMN template VARCHAR(50) DEFAULT 'custom', ADD COLUMN subject VARCHAR(255) DEFAULT '', ADD COLUMN email_content TEXT, ADD COLUMN sender_name VARCHAR(100) DEFAULT 'Security Team', ADD COLUMN sender_email VARCHAR(100) DEFAULT 'noreply@trainme.com', ADD COLUMN landing_page_url VARCHAR(500), ADD COLUMN scheduled_at TIMESTAMP NULL, ADD COLUMN sent_at TIMESTAMP NULL, ADD COLUMN completed_at TIMESTAMP NULL, ADD COLUMN total_recipients INT DEFAULT 0, ADD COLUMN total_sent INT DEFAULT 0, ADD COLUMN total_clicks INT DEFAULT 0, ADD COLUMN total_reports INT DEFAULT 0;

